type NextImageProps = ComponentProps<typeof Image>;
export type RemoteImageId = keyof typeof remoteImages;

export function RemoteImage({
  id,
  placeholder,
  fill,
  sizes,
  className,
}: {
  id: RemoteImageId;
  placeholder?: NextImageProps['placeholder'];
  fill?: NextImageProps['fill'];
  sizes: NextImageProps['sizes'];
  className?: string;
}) {
  const { width, height, ...imageProps } = remoteImages[id];
  const sizeProps = fill ? { fill: true } : { width, height };

  return (
    // eslint-disable-next-line jsx-a11y/alt-text
    <Image
      {...imageProps}
      {...sizeProps}
      sizes={sizes}
      placeholder={placeholder ?? 'blur'}
      className={cn(className)}
    />
  );
}
